#include <Servo.h>

// Pines (ajústalos si usaste otros)
const int PIN_TRIG = 3;
const int PIN_ECHO = 4;
const int PIN_SERVO = 6;

Servo brazo;

long medirDist(){
  digitalWrite(PIN_TRIG, LOW);
  delayMicroseconds(2);
  digitalWrite(PIN_TRIG, HIGH);
  delayMicroseconds(10);
  digitalWrite(PIN_TRIG, LOW);
  long dur = pulseIn(PIN_ECHO, HIGH, 30000); // timeout ~30 ms
  if(dur == 0) return 999;
  float cm = (dur/2.0) / 29.1;
  return (long)cm;
}

void saludar(){
  // agita el brazo 3 veces
  for(int i=0;i<3;i++){
    brazo.write(40); delay(220);
    brazo.write(110); delay(220);
  }
  brazo.write(80); // posición de reposo
}

void setup(){
  pinMode(PIN_TRIG, OUTPUT);
  pinMode(PIN_ECHO, INPUT);
  brazo.attach(PIN_SERVO);
  brazo.write(80);
}

void loop(){
  long d = medirDist();
  if(d < 50){           // a menos de 50 cm, saluda
    saludar();
    delay(1200);        // pequeño descanso para no disparar continuo
  } else {
    brazo.write(80);
    delay(60);
  }
}
